import React, { useState } from 'react'

function InputArea(){
    const [inputNote, setInputNote] = useState({
        title: "",
        content: ""
    })

    function handleChange(event){
        setInputNote(event.target.value)
    }

    return <div className='note'>
        <form>
            <input onChange={handleChange} type="text" placeholder="Title" value={inputNote.title}></input>
            <textarea onChange={handleChange} type="text" placeholder="Content" value={inputNote.content}></textarea>
            <button>Add</button>
        </form>
    </div>
}

export default InputArea;
